import javax.crypto.Cipher
import javax.crypto.spec.SecretKeySpec
import java.util.Base64
import java.security.Key

// Define a 16-byte (128-bit) AES key
String encryptionKey = "6IjvhAVokaBF7/63opRorQ=="  // Replace with a secure key
Key aesKey = new SecretKeySpec(encryptionKey.getBytes("UTF-8"), "AES")

// Initialize cipher in AES/ECB mode
Cipher cipher = Cipher.getInstance("AES/ECB/PKCS5Padding")
cipher.init(Cipher.ENCRYPT_MODE, aesKey)

// Get the SSN field from the record
String ssn = record.getValue("ssn")

// Encrypt if SSN is present
if (ssn != null) {
    byte[] encryptedBytes = cipher.doFinal(ssn.getBytes("UTF-8"))
    String encryptedBase64 = Base64.encoder.encodeToString(encryptedBytes)
    record.setValue("ssn", encryptedBase64)
}

return record
